import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router';
import { ApplicationState } from '../configureStore';
import { allDispatch } from '../configureStore/extensions/dispatch';
import './static/navbar.scss';

const NavbarComponent = () => {
  const dispatch = useDispatch();
  const state = useSelector((state: ApplicationState) => state.default);
  const history = useHistory();
  const click = (type: string, name: string, params: string) => {
    switch (type) {
      case 'drawer':
        if (!state.drawer.active || state.drawer.active === 2) {
          allDispatch.defaultDispatch(dispatch, { active: 1 }, 'drawer');
        } else {
          allDispatch.defaultDispatch(dispatch, { active: 2 }, 'drawer');
        }
        break;
      case 'router':
        if (params) {
          history.push(`${name}${params}`);
        } else {
          history.push(name);
        }
        break;
      default:
        break;
    }
  };
  return state.hidden ? null : state.token ? null : (
    <div className="navbar">
      <div className="group">
        <img
          src="data:image/png;base64,"
          alt=""
          onClick={click.bind('', 'router', '/loading/', 'home')}
        />
        <div className="sub-navbar">
          <a href="">Support</a>
          <a href="">Company</a>
        </div>
      </div>
      <div className="sub-navbar">
        <button className="language">
          <i className="fas fa-globe-asia"></i>
        </button>
        <a href="">contact us</a>
        <button
          className="web"
          onClick={click.bind('', 'router', '/loading/', 'access-login')}
        >
          <span>go to application</span>
          <i className="fas fa-arrow-right"></i>
        </button>
        <button className="mobile" onClick={click.bind('', 'drawer')}>
          <i className="fas fa-bars"></i>
        </button>
      </div>
    </div>
  );
};

export default NavbarComponent;
